<?phpif ( ! defined('BASEPATH')) exit('No direct script access allowed');class MainModel extends CI_Model {    function __construct() {        $this->products = 'products';    }    public function getAllProducts(){       $query = $this->db->get('products');       if($query->num_rows() > 0){           return $query->result_array();       }    }    public function getProductDetails($product_id){        $query = $this->db->query("SELECT * FROM `products` WHERE `idproducts` = '$product_id'");        return $query->result_array();    }    public function addProduct($data = array()){        if(!array_key_exists("create_on",$data)){            $data['create_on'] = date("Y-m-d H:i:s");        }        if(!array_key_exists("modified_on",$data)){            $data['modified_on'] = NULL;        }        $insert = $this->db->insert($this->products,$data);        if($insert){            return true;        }else{            return false;        }    }    public function login($username,$password){        $query = $this->db->query("SELECT * FROM `user` WHERE `username` = '$username' AND `password` = '$password'");        return $query->result_array();    }    public function getCategory(){        $query = $this->db->get('category');        if($query->num_rows() > 0){            return $query->result_array();        }    }    public function getProductsForAdminPanel(){        $query = $this->db->get('products');        if($query->num_rows() > 0){            return $query->result_array();        }    }    public function deleteProduct($id){        return $this->db->query("DELETE from `products` WHERE `idproducts` = '$id'");    }}?>